package Intprograms;

public class Swappingnumbers2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int a=23,b=56;
          System.out.println("Before swapping the values:" +a +" "+b);
          a=a+b;
          b=a-b;
          a=a-b;
          System.out.println("After Swapping the values:"+a+" "+b);
      
	}

}
