package learning;

public class Arthematicoperat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Modulus = remainder
		// Division = quotient
		int a = 8, b = 9, c = 4;
		int x = a / c;// 2
		int y = b % c;// 1
		int z = a % c;// 0
		System.out.println("Division of x is : " + x);
		System.out.println("Modulus of y is : " + y);
		System.out.println("Modulus of z is : " + z);

	}

}
